msg.payload = {};
msg.payload.command = {};

var u = 'http://{s}.tile.openstreetmap.fr/hot/{z}/{x}/{y}.png';
var o = { maxZoom: 19, attribution: '&copy; OpenStreetMap'};

msg.payload.command.map = {name:"OSMhot", url:u, opt:o};
msg.payload.command.layer = "OSMhot";

return msg;

msg.payload.lat = msg.payload.latitude;
msg.payload.lon = msg.payload.longitude;
msg.payload.layer = "earthquake";
msg.payload.name = msg.payload.id;
msg.payload.icon = "globe";
msg.payload.iconColor = "orange";

delete msg.payload.latitude;
delete msg.payload.longitude;	
return msg;

if(msg.clearAll === true){
 flow.set('persistant_settings', {});
 return [msg, {payload: {}}];
}

var settings = flow.get('persistant_settings') || {};

if(msg.topic == 'get'){
 msg.topic = msg.payload;
 msg.payload = typeof settings[msg.payload] == 'undefined' ? null : settings[msg.payload];
 return [msg, null]
}else{
 settings[msg.topic] = msg.payload;
 flow.set('persistant_settings', settings);
 return [msg, {payload: settings}];
}

