{
    "currentState": "running",
    "states": {
        "stopped": {
            "on": "running"
        },
        "running": {
            "off": "stopped"
        }
    },
    "data": {
        "hot": 28,
        "cool": 26,
        "min": 0,
        "max": 100
    },
    "methods": {
        "on": [
            "sm.fill = 'green';",
            "sm.text = 'on';",
            "output = false;"
        ],
        "off": [
            "sm.fill = 'grey';",
            "sm.text = 'off';",
            "output = false;"
        ],
        "setHot": [
            "sm.data.hot = msg.payload;",
            "sm.fill = 'grey';",
            "sm.text = 'hot = '+sm.data.hot;"            
        ],
        "setCool": [
            "sm.data.cool = msg.payload;",
            "sm.fill = 'grey';",
            "sm.text = 'cool = '+sm.data.cool;"            
        ],
        "default": [
            "if(sm.currentState === 'running') {",
            "   sm.temp = Math.round(msg.payload*10)/10;",
            "   if(msg.payload > sm.data.hot) {",
            "       msg.payload = sm.data.max;",
            "       sm.fill = 'yellow';",
            "   } else if (msg.payload < sm.data.cool) {",
            "       msg.payload = sm.data.min;",
            "       sm.fill = 'blue';",
            "   } else {",
            "       sm.inr = sm.data.hot - sm.data.cool;",
            "       sm.outr = sm.data.max - sm.data.min;",
            "       sm.rel = msg.payload - sm.data.cool;",
            "       msg.payload = Math.round(sm.rel * sm.outr / sm.inr);",
            "       if (msg.payload < 30) msg.payload = 0;",
            "       sm.fill = 'green';",
            "   }",
            "   sm.text = 'temp '+sm.temp+' [ '+sm.data.cool+'-'+sm.data.hot+' ] pwm '+msg.payload;",
            "   output = true;",
            "} else {",
            "   sm.fill = 'grey';",
            "   sm.text = 'off';",
            "   output = false",
            "}"
        ],
        "status": {
            "fill": {
                "get": "sm.fill"
            },
            "shape": "dot",
            "text": {
                "get": "sm.text;"
            }
        }
    }
}

{
    "currentState": "stopped",
    "states": {
        "stopped": {
            "start": "started"
        },
        "started": {
            "run": "running",
            "end": "stopped"
        },
        "running": {
            "run": "running",
            "end": "stopped"
        }
    },
    "data": {
      "step": 10
    },
    "methods": {
        "start":  [
            "if (sm.currentState === 'started') {",
            "   node.send(msg);",
            "   resume('run', msg);",
            "}"
        ],
        "run": [
            "if (msg.payload.brightness < 100) {",
            "   msg.payload.brightness = msg.payload.brightness + sm.data.step;",
            "   if (msg.payload.brightness > 100) msg.payload.brightness=100;",
            "   sm.tr = 'run';",
            "} else {",
            "   sm.tr = 'end';",
            "}",
            "timeout.interval = setTimeout(function() {",
            "   if (sm.tr === 'run') node.send(msg);",
            "   resume(sm.tr, msg);",
            "}, 1000);"
        ],
        "onAfterTransition": "output=false;",
        "status": {
            "fill": {
                "get": "sm.currentState === 'running' ? 'green' : 'grey'"
            },
            "shape": "dot",
            "text": {
                "get": "sm.currentState"
            }
        }
    }
}

{
    "currentState": "stopped",
    "states": {
        "stopped": {
            "on": "started"
        },
        "started": {
            "inc": "counting",
            "off": "stopped"
        },
        "counting": {
            "inc": "counting",
            "off": "stopped"
        }
    },
    "data": {
        "prev_time": null,
        "time": 0,
        "seconds": 0,
        "interval": 1,
        "interval_output": true,
        "hms_format": true
    },
    "methods": {
        "init": [
            "sm.calc_time = function() {",
            "   var now = Date.now();",
            "   sm.data.time += now - sm.data.prev_time;",
            "   sm.data.prev_time = now;",
            "   sm.data.seconds = Math.round(sm.data.time / 1000);",
            "};",
            "sm.sec2hhmmss = function(sec) {",
                "var t = {};",
                "t.h = pad(Math.floor(sec / 3600));",
                "sec %= 3600;",
                "t.m = pad(Math.floor(sec / 60));",
                "t.s = pad(sec % 60);",
                "return t.h+':'+t.m+':'+t.s;",
            "};"
        ],
        "on": [
            "if (sm.currentState === 'started') {",
            "   sm.data.prev_time = Date.now();",
            "   resume('inc', msg);",
            "}",
            "output = false;"
        ],
        "inc": [
            "timeout.interval = setTimeout(function() {",
            "   sm.calc_time();",
            "   msg.data = sm.data;",
            "   if (sm.data.interval_output) {",
            "       msg.payload = sm.data.hms_format ? sm.sec2hhmmss(sm.data.seconds): sm.data.seconds;",
            "       node.send(msg);",
            "   }",
            "   resume('inc', msg);",
            "}, sm.data.interval*1000);",
            "output = false;"
        ],
        "off": [
            "clearTimeout(timeout.interval);",
            "sm.calc_time();",
            "msg.payload = sm.data.hms_format ? sm.sec2hhmmss(sm.data.seconds): sm.data.seconds;"
        ],
        "reset": [
            "sm.data.time = 0;",
            "sm.data.seconds = 0;"
        ],
        "status": {
            "fill": {
                "get": "sm.currentState === 'counting' ? 'green' : 'grey';"
            },
            "shape": "dot",
            "text": {
                "get": "'time ' + (sm.data.hms_format ? sm.sec2hhmmss(sm.data.seconds): sm.data.seconds);"
            }
        }
    }
}

